import { Button } from 'antd';
import { useState,useRef } from 'react';
import QrReader from 'react-qr-reader';
  
export const QrCode=()=>{
  const qrRef = useRef();
  
  const handleError = ()=>{
    console.log('error in qr scanning')
  }
  const handleScanner = ()=>{
    console.log('error in qr scanning')
  }
  return (
    <div className="App">
     <Button>Scan Qr</Button>
     <QrReader
    ref={qrRef}
    delay={300}
    style={{width:"300px"}}
    onError={handleError}
    onScan={handleScanner}
    lagacyMode
    />
    </div>
  );
}
