import { Button,Card } from 'antd'
import style from './managerHome.module.css'
import React from 'react'
import { MechanicTableForManager } from './mechanicTableForManager'
import { useNavigate } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { isAuth } from '../../redux/pageSlice'
import axios from 'axios'
import { useEffect } from 'react'
import jsPDF from 'jspdf'
import 'jspdf-autotable'
export const ManagerHomePage=()=>{
  const navigate = useNavigate()
  const dispatch = useDispatch();
  const handleLogout = ()=>{
   dispatch(isAuth(false))
    navigate('/')
  const  [breakdownTIme,setBreakdownTime]=useState(0);  
  
  const startTime = ()=>{
    axios.get('')
  }

  const getBreakdownTime=()=>{
    const bTime = localStorage.getItem('breakdown_start_time');
    console.log(bTime.split(" "),'ppppppppppppppp')
    console.log('hello')
  }
  useEffect(()=>{
    getBreakdownTime()
},[])

  }
  const downloadPdf =()=>{
    const doc = new jsPDF();
    doc.test("student Details",20,10);

    doc.save('table.pdf')
  }
  return (
    <>
    <div className={style.mainHeading}>
    <h2 >Manager & Executive (Seniors){localStorage.getItem("breakdown_start_time").split(" ")}</h2>
    </div>
    <div style={{ background: '#ECECEC', padding: '30px' }}>
    <Card title="Downtime Status" bordered={false} style={{ width: 300 }}>
      <p>Downtime duration: <span>{localStorage.getItem("breakdown_start_time")} To {localStorage.getItem("repair_start_time")} </span> </p>
    </Card>
  </div>,
        <Button style={{margin:"2%",float:"right"}} type='primary' onClick={handleLogout}>Logout</Button>
        <Button style={{margin:"2%",float:"right"}} type='primary' onClick={downloadPdf}>Download Pdf</Button>
   <MechanicTableForManager/>
    </>
  )
}
