import React from 'react'
import { Table,Form,Input,Select,Button } from 'antd'
export const ViewInventory = () => {
 const columns = [
    {
    name:"Category",
    dataIndex:"category"
    },
    {
    name:"For Machine",
    dataIndex:"for_machine"
    },
    {
    name:"Part Change",
    dataIndex:"part_change"
    },
    {
    name:"Part Name",
    dataIndex:"part_name"
    },
    {
    name:"Description",
    dataIndex:"description"
    },
    {
    name:"Cost",
    dataIndex:"cost"
    },
    {
    name:"Last Received Date",
    dataIndex:"last_received_data"
    },
    {
    name:"Stock Balance",
    dataIndex:"stock_balance"
    },
 ]
 const data = [{category:3,for_machine:2,part_name:"cutter"}]
  
 const onFinish = ()=>{
   console.log('onSearch')
 }

 return (
    <div style={{margin:"auto",width:"50% "}}>
      <h1>Store Executive   View Inventory</h1>
      <Form
      //   className="tool_record_form"
        name="tool-form"
        layout="inline"
        onFinish={onFinish}
        // initialValues={{
        //   price: {
        //     number: 0,
        //     currency: 'rmb',
        //   },
        // }}
      >
      
        <Form.Item
          name="category"
          label="Category"
        >
       <Select>
            <Select.Option value="demo">Demo</Select.Option>
            <Select.Option value="demo">Demo2</Select.Option>
            <Select.Option value="demo">Demo3</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item
          name="for_machine"
          label="For Machine"
        >
       <Select>
            <Select.Option value="demo">Demo</Select.Option>
            <Select.Option value="demo">Demo2</Select.Option>
            <Select.Option value="demo">Demo3</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item
          name="part_code"
          label="Part Code"
        >
          <Input />
        </Form.Item>
        <Form.Item>
          <Button className="search_btn" htmlType="submit">
            Search
          </Button>
        </Form.Item>
      </Form>
      <Button>Export Report</Button>
        <Table
        columns={columns}
        dataSource={data}
        />
    </div>
  )
}
