import React, { useEffect, useState } from "react";
import { Button, Form, Input,Modal, TimePicker } from "antd";
import { useNavigate,Link } from "react-router-dom";
import styles from "./home.module.css";
import dayjs from 'dayjs';
import { NotificationConfirmation } from "./notificationConfirmation";
export const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  const [selectedTime,setSelectedTime]=useState(dayjs(moment().format('hh:mm:ss')))
  const date = new Date();
  useEffect(()=>{
    setSelectedTime(moment().format('hh:mm:ss'))
    console.log(moment().format('hh:mm:ss'));
  },[date])

  const showModal = () => {
    setIsModalOpen(true);
  };

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const layout = {
    labelCol: {
      span: 8
    },
    wrapperCol: {
      span: 16
    }
  };

  const validateMessages = {
    required: "${label} is required!",
    types: {
      email: "${label} is not a valid email!",
      number: "${label} is not a valid number!"
    },
    number: {
      range: "${label} must be between ${min} and ${max}"
    }
  };

  const onFinish = (values) => {
    // setDataBase(values);
    // values.preventDefaul;
    // localStorage.setItem('items', JSON.stringify([ ...oldData, ...obj ]));

    let oldData = JSON.parse(localStorage.getItem("userData"));
    localStorage.setItem("userData", JSON.stringify([...oldData, values.user]));
    console.log(values, "-----------");
    if (values) {
      navigate("/details");
    }
  };
  return (
    <>
      <div className={styles.container}>
        {/* <Button className={styles.home_btn} type="primary">
          First
        </Button>
        <Button className={styles.home_btn} type="primary">
          Second
        </Button> */}
        <Form
          className={styles.form_container}
          layout="vertical"
          name="nest-messages"
          onFinish={onFinish}
          style={{
            maxWidth: 600
          }}
          validateMessages={validateMessages}
        >
          <Form.Item
            name={["user", "line_no"]}
            label="Line Number"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "machine_number"]}
            label="Machine Number"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "mechanic"]}
            label="Mechanic"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "breakdown_time"]}
            label="Breakdown Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <TimePicker defaultValue={dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')} size="large" />
          </Form.Item>
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              htmlType="submit"
            >
              Send Notification
            </Button>
          </Form.Item>
        </Form>
        <Link
            className={styles.link_class}
            onClick={showModal}
            to="/notification_confirmation"
          >
            Forgot Password?
          </Link>
        <Modal
          width={320}
          title="Reset Password Using Email"
          open={isModalOpen}
          onOk={handleOk}
          onCancel={handleCancel}
          footer={null}
        >
          <NotificationConfirmation />
        </Modal>
      </div>

    </>
  );
};
