import { Button,Card } from 'antd'
import style from './managerHome.module.css'
import React from 'react'
import { MechanicTableForManager } from './mechanicTableForManager'
import { useNavigate } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { isAuth } from '../../redux/pageSlice'
import axios from 'axios'
import { useEffect } from 'react'
import { useState } from 'react'

export const ManagerHomePage=()=>{
  const navigate = useNavigate()
  const dispatch = useDispatch();
  const[data,setData]=useState(null)
  const[breakdownStartTime,setBreakdownTime]=useState(null)
  const handleLogout = ()=>{
   dispatch(isAuth(false))
    navigate('/')
  const  [breakdownTIme,setBreakdownTime]=useState(0);  
  
  const startTime = async()=>{
    axios.get('http://localhost:5000/api/supervisor_table').then(res=>setBreakdownTime(res.data.breakdown_start_time)).then(err=>console.log(err,'error'))
  }
  const loadData = async()=>{
    axios.get('http://localhost:5000/api/mechanic_table').then(res=>console.log(res.data)).then(err=>console.log(err,'error'))
   
  }
console.log("hello")
  // const getBreakdownTime=()=>{
  //   const bTime = localStorage.getItem('breakdown_start_time');
  //   console.log(bTime.split(" "),'ppppppppppppppp')
  //   console.log('hello')
  // }
  useEffect(()=>{
    // getBreakdownTime();
    loadData();
    startTime();
},[])
  }
  
  return (
    <>
    <div className={style.mainHeading}>
    <h2 >Manager & Executive (Seniors)</h2>
    </div>
    <div style={{ background: '#ECECEC', padding: '30px' }}>
    <Card title="Downtime Status" bordered={false} style={{ width: 300 }}>
      <p>Downtime duration: <span>{} To {breakdownStartTime} </span> </p>
    </Card>
  </div>,
        <Button style={{margin:"2%",float:"right"}} type='primary' onClick={handleLogout}>Logout</Button>
        
   <MechanicTableForManager data={data}/>
    </>
  )
}
