import React, { useState, useEffect } from "react";
import axios from 'axios'
import { Button, Form, Input, Radio, Modal } from "antd";
import { Link, Navigate, useNavigate } from "react-router-dom";
import login_img from '../img/images.jpg'
import styles from "./feature.module.css";
import { ForgotPassword } from "./forgot_password";
import {degination} from '../redux/pageSlice'
import {useDispatch} from 'react-redux';
export const Login = () => {
  const [data,setData]=useState(null);
  const loadData = async()=>{
    axios.get('http://localhost:5000/api/get').then(res=>console.log(res.data,'result')).then(err=>console.log(err,'error'))
  }
  useEffect(()=>{
    loadData();
  },[])
  const [form] = Form.useForm();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const dispatch = useDispatch()
  const navigate = useNavigate('');
  // const [employee_name, setEmployee_name] = useState("");

  useEffect(() => {
    form.setFieldsValue({
      employee_name: "Sam"
    });
  }, []);
  const showModal = () => {
    setIsModalOpen(true);
  };

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  // const layout = {
  //   labelCol: {
  //     span: 8
  //   },
  //   wrapperCol: {
  //     span: 16
  //   }
  // };

  // const validateMessages = {
  //   required: "${label} is required!",
  //   types: {
  //     email: "${label} is not a valid email!",
  //     number: "${label} is not a valid number!"
  //   },
  //   number: {
  //     range: "${label} must be between ${min} and ${max}"
  //   }
  // };

  const onFinish = (values) => {
    console.log(values, "login Data++++++++++++");
    if(values){
      localStorage.setItem('loginData',values)
dispatch(degination(values.user.designation))
      navigate('/qrcode')
    }
  };

  return (
    <>
        <div className={styles.main_container}>
      <div className={styles.container}>


        <h2>Login</h2>
        <Form
          form={form}
          className={styles.form_container}
          layout="vertical"
          name="nest-messages"
          onFinish={onFinish}
          style={{
            maxWidth: 600
          }}
          // validateMessages={validateMessages}
          >
          <Form.Item name={["user", "designation"]} label="Your Designation">
            <Radio.Group>
              <Radio.Button value="Supervisor">Supervisor</Radio.Button>
              <Radio.Button value="Machanic">Mechanic</Radio.Button>
            </Radio.Group>
          </Form.Item>
          <Form.Item name={["user", "card_number"]} label="Card Number">
            <Input />
          </Form.Item>
          <Form.Item name={["user", "employee_name"]} label="Employee Name">
            <Input />
          </Form.Item>
          <Form.Item
            name={["user", "password"]}
            label="Your Password"

            rules={[
              {
                required: true
              }
            ]}
            >
            <Input  type="password" />
          </Form.Item>
          <Form.Item
            wrapperCol={{
              offset: 1
            }}
          >
            <Button
              className={styles.feature_btn}
              type="primary"
              htmlType="submit"
              >
              Sign in
            </Button>
          </Form.Item>
        </Form>
        <p className={styles.feature_footer_link}>
          <Link
            className={styles.link_class}
            onClick={showModal}
            to="/forgot_password"
          >
            Forgot Password?
          </Link>
          <Link className={styles.link_class} to="/signup">
            Signup
          </Link>
        </p>

        <Modal
          width={320}
          // visible={isModalOpen}
          title="Reset Password Using Email"
          open={isModalOpen}
          onOk={handleOk}
          onCancel={handleCancel}
          footer={null}
          >
          <ForgotPassword />
        </Modal>
        </div>
            </div>
    </>
  );
};
