import React, { useEffect, useState } from "react";
import { Button, Form, Input,Modal,Select,Space, TimePicker } from "antd";
import { useNavigate } from "react-router-dom";
import styles from "./home.module.css";
import dayjs from 'dayjs';
import {useSelector} from 'react-redux';
import { NotificationConfirmation } from "./notificationConfirmation";
export const MachanicForm = () => {
  const [open, setIsOpen] = useState(false);
  // const option = {Select}
  const navigate = useNavigate();
  const [form] = Form.useForm();
  const [startTime,setStartTime] =useState(null);
  const data = useSelector(state=>state.pageReducer.data);
  const [selectedTime,setSelectedTime]=useState(dayjs(moment().format('hh:mm:ss')))
  const date = new Date();
  useEffect(()=>{
    setSelectedTime(moment().format('hh:mm:ss'))


  },[date])

  const time =new Date()
  var hour = time.getHours();
  var minute = time.getMinutes();
  var second = time.getSeconds();
  const layout = {
    labelCol: {
      span: 8
    },
    wrapperCol: {
      span: 16
    }
  };

  const validateMessages = {
    required: "${label} is required!",
    types: {
      email: "${label} is not a valid email!",
      number: "${label} is not a valid number!"
    },
    number: {
      range: "${label} must be between ${min} and ${max}"
    }
  };
  useEffect(()=>{
    console.log(data,'inn home component++++++');
    console.log(data,'data in machanic page')
    dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')

    setStartTime(`${hour}/${minute}/${second}`)
    console.log(startTime,'+++++++');
  },[])

  const { Option } = Select;
const handleChange = (value) => {
  console.log(`selected ${value}`);
};

  const handleOk = () => {
    setIsOpen(false);
  };

  const handleCancel = () => {
    setIsOpen(false);
  };

  const onFinish = (values) => {
    // setDataBase(values);
    // values.preventDefaul;
    // localStorage.setItem('items', JSON.stringify([ ...oldData, ...obj ]));


    let oldData = JSON.parse(localStorage.getItem("userData"));
    localStorage.setItem("userData", JSON.stringify([...oldData, values.user]));
    console.log(values, "-----------");
    if (values) {
      setIsOpen(true);
      // navigate("/notification_confirmation")
    }
  };
  return (
    <>
       {data ? <div className={styles.container}>
      <Form
      form = {form}
          className={styles.form_container}
          layout="vertical"
          name="nest-messages"
          onFinish={onFinish}
          initialValues={data}
          style={{
            maxWidth: 600
          }}
          validateMessages={validateMessages}
        >
           <Form.Item
            label="Line Number"
            name="line_no"
            rules={[{ required: true, message: "Please input your name!" }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="machine_no"
            label="Machine Number"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="machine_type"
            label="Machine Type"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input  
            />
          </Form.Item>
          <Form.Item
            name={ "start_time"}
            label="Start Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input defaultValue = {`${hour}/${minute}/${second}`} type="time" />
          </Form.Item>
                <Form.Item
                  name={ "operation"}
                  label="Operation"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input 
                  />
                </Form.Item>
                <Form.Item
            name={ "breakdown_time"}
            label="Breakdown Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <TimePicker defaultValue={dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')} size="large" />
          </Form.Item>
          <Form.Item
                  name={"breakdown_reason"}
                  label="Breakdown Reason"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Select
    mode="multiple"
    style={{
      width: '100%',
    }}
    placeholder="select one country"
    // defaultValue={['Overrunning machines']}
    onChange={handleChange}
    optionLabelProp="label"
  >
    <Option value="china" label="Wiring Fault">
      <Space>
       wiring fault
      </Space>
    </Option>
    <Option value="usa" label="Untrained operators">
      <Space>
      Untrained operators
      </Space>
    </Option>
    <Option value="japan" label="Poor maintenance">
      <Space>
      Poor maintenance
      </Space>
    </Option>
    <Option value="korea" label="Overrunning machines">
      <Space>
      Overrunning machines
      </Space>
    </Option>
  </Select>
                </Form.Item>
          <Form.Item
                  name={ "action_taken"}
                  label="Action Taken"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input />
                </Form.Item>
          <Form.Item
                  name={"part_replaced"}
                  label="Part Replaced"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                   <Select
    mode="multiple"
    style={{
      width: '100%',
    }}
    placeholder="select one country"
    // defaultValue={['wire']}
    onChange={handleChange}
    optionLabelProp="label"
  >
    <Option value="china" label="main Part">
      <Space>
       main Part
      </Space>
    </Option>
    <Option value="usa" label="Wire">
      <Space>
      Wire
      </Space>
    </Option>
    <Option value="japan" label="Head">
      <Space>
      Head
      </Space>
    </Option>
    <Option value="korea" label="machines">
      <Space>
      machines
      </Space>
    </Option>
  </Select>
                </Form.Item>
          <Form.Item
                  name={["user", "no_of_spare_parts"]}
                  label="No Of Spare Parts"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input />
                </Form.Item>
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              // htmlType="submit"
            >
             Machine Repaired
            </Button>
          </Form.Item>
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              htmlType="submit"
            >
              Submit Record
            </Button>
          </Form.Item>
        </Form>
        <Modal open={open} title="Reset Password Using Email"
          onOk={handleOk}
          onCancel={handleCancel}>
        <NotificationConfirmation/>
      </Modal>
      </div>:<h1>Coming soon....</h1>}

    </>
  );
};
