import React, { useEffect, useState } from "react";
import { Button, Form, Input,Modal,Select,Space, TimePicker } from "antd";
import {useSelector} from 'react-redux';
import { useNavigate } from "react-router-dom";
import styles from "./home.module.css";
import dayjs from 'dayjs';
import { NotificationConfirmation } from "./notificationConfirmation";
export const SupervisorForm = () => {
  const [open, setIsOpen] = useState(false);
  const [form] = Form.useForm();
  const [selectedTime,setSelectedTime]=useState(dayjs(moment().format('hh:mm:ss')));
  const data = useSelector(state=>state.pageReducer.data);
  const date = new Date();
  useEffect(()=>{
    setSelectedTime(moment().format('hh:mm:ss'))
    console.log(moment().format('hh:mm:ss'));
  },[date])
  

  const layout = {
    labelCol: {
      span: 8
    },
    wrapperCol: {
      span: 16
    }
  };

  const validateMessages = {
    required: "${label} is required!",
    types: {
      email: "${label} is not a valid email!",
      number: "${label} is not a valid number!"
    },
    number: {
      range: "${label} must be between ${min} and ${max}"
    }
  };
  useEffect(()=>{
    console.log(data,'inn superviser component++++++')
  },[])

  const { Option } = Select;
const handleChange = (value) => {
  console.log(`selected ${value}`);
};

  const handleOk = () => {
    setIsOpen(false);
  };

  const handleCancel = () => {
    setIsOpen(false);
  };

  const onFinish = (values) => {
    // setDataBase(values);
    // values.preventDefaul;
    // localStorage.setItem('items', JSON.stringify([ ...oldData, ...obj ]));
  

    let oldData = JSON.parse(localStorage.getItem("userData"));
    localStorage.setItem("userData", JSON.stringify([...oldData, values.user]));
    console.log(values, "-----------");
    if (values) {
      setIsOpen(true);
      // navigate("/notification_confirmation")
    }
  };
  const line1 ={name:"aaa"}
  const line2 ={name:"aaa"}

  const handlemechanicChange = (e)=>{
console.log(e.target.value)
  }
  return (
    <>
       {data ? <div className={styles.container}>
        {/* <Button className={styles.home_btn} type="primary">
          First
        </Button>
        <Button className={styles.home_btn} type="primary">
          Second
        </Button> */}
      <Form
      form = {form}
          className={styles.form_container}
          layout="vertical"
          name="nest-messages"
          onFinish={onFinish}
          initialValues={data}
          style={{
            maxWidth: 600
          }}
          validateMessages={validateMessages}
        >
          <Form.Item
            name="line_no"
            label="Line Number"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input disabled />
          </Form.Item>
          <Form.Item
          defaultValue="props.data.machine_no"
            name="machine_no"
            // defaultValue={ props.data.machine_id}
            label="Machine Number"
            
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input disabled />
          </Form.Item>
         
          <Form.Item
            name="mechanic "
            // defaultValue={ props.data.machine_id}
            label="mechanic"
            
            rules={[
              {
                required: true
              }
            ]}
          >
             <Select
    mode="multiple"
    style={{
      width: '100%',
    }}
    placeholder="select one country"
    // defaultValue={['Overrunning machines']}
    onChange={handlemechanicChange}
    optionLabelProp="label"
  >
    <Option value="hand_wheel_siruba_O/L	
" label="Hand wheel siruba O/L	
">
      <Space>
      Hand wheel siruba O/L	
      </Space>
    </Option>
    <Option value="GKC83_Front_cover_siruba O/L	
" label="GKC83 Front cover siruba O/L	
">
      <Space>
      GKC83 Front cover siruba O/L	
      </Space>
    </Option>
    <Option value="KB15B_door_lock_siruba_O/L	
" label="KB15B door lock siruba O/L	
">
      <Space>
      Poor maintenance
      </Space>
    </Option>
    <Option value="KQ05_Fixing_guard_feed_dog_siruba_O/L	
" label="KQ05 Fixing guard feed dog siruba O/L	
">
      <Space>
      KL201 connecting holder siruba O/L	
      </Space>
    </Option>
    <Option value="9008_oil_filter_O/L	
" label="9008 oil filter O/L	
">
      <Space>
      9008 oil filter O/L	
      </Space>
    </Option>
    <Option value="KN18A_THD_takeup_lever_siruba_O/L	
" label="KN18A THD takeup lever siruba O/L	
">
      <Space>
      KN18A THD takeup lever siruba O/L	
      </Space>
    </Option>
    <Option value="KJ01B_connector_siruba_O/L	
" label="KJ01B connector siruba O/L	
">
      <Space>
      KJ01B connector siruba O/L	
      </Space>
    </Option>
    <Option value="KR19_upper_blade_Asm_siruba_O/L	
" label="KR19 upper blade Asm siruba O/L	
">
      <Space>
      KR19 upper blade Asm siruba O/L		
      </Space>
    </Option>
    <Option value="KS01_Presser_foot_arm_siruba_O/L"
     label="KS01 Presser foot arm siruba O/L	">
      <Space>
      KS01 Presser foot arm siruba O/L	
      </Space>
    </Option>
    <Option value="KJ26_Upper_looper_holder_siruba_O/L" 
label="KN18A THD takeup lever siruba O/L">
      <Space>
      KJ26 Upper looper holder siruba O/L	
      </Space>
    </Option>
    <Option value="KG236_Needle_holder_siruba_O/L	"
     label="KG236 Needle holder siruba O/L	">
      <Space>
      KG236 Needle holder siruba O/L	
      </Space>
    </Option>
    <Option value="H208_Front_feed_dog_siruba_O/L	"
     label="H208 Front feed dog siruba O/L">
      <Space>
      H208 Front feed dog siruba O/L	
      </Space>
    </Option>
    <Option value="KN20, 5th Cam lever siruba O/L	"
     label="KN20, 5th Cam lever siruba O/L">
      <Space>
      KN20, 5th Cam lever siruba O/L	
      </Space>
    </Option>
    <Option value="KF96 Needle bar siruba"
     label="KF96 Needle bar siruba">
      <Space>
      KF96 Needle bar siruba	
      </Space>
    </Option>
  </Select>
          </Form.Item>
                <Form.Item
            name="breakdown_time"
            label="Breakdown Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <TimePicker defaultValue={dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')} size="large" />
          </Form.Item>
          
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              htmlType="submit"
            >
              Submit Record
            </Button>
          </Form.Item>
        </Form>
        <Modal open={open} title="Reset Password Using Email"
          onOk={handleOk}
          onCancel={handleCancel}>
        <NotificationConfirmation/>
      </Modal>
      </div>:<h1>Coming soon....</h1>}
     
    </>
  );
};
