import React,{useEffect, useState} from 'react'
import { Button, Form, Select,Input } from 'antd';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';
const { Option } = Select;
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};
export const PartIssue = () => {
   const[data,setData]=useState([]);
  //  const navigate = useNavigate()
   const [form] = Form.useForm();
   const onMechanicChange = (value) => {
    console.log(value)
  };
  useEffect(()=>{
    axios.get('http://localhost:5000/api/tool_table').then(res=>setData(res.data))
    // setPostData(data.filter(i=>i.tool_code==toolCode))
      // console.log(data,'data data data')
    // console.log(postData,'kkkkkkkkkkkkkkkkkkkkkk')
  },[])
  const onFinish = async() => {
    console.log(data,'onFinish')
    // let datas ={
    //   id: data[0].id,
    //   value: data[0].value-1,        
    // }
    // try {
    //   await axios.put('http://localhost:5000/api/issueTool/', datas)
    // } catch (error) {
    //   console.log(error)
    // }
  };
    const onReset = () => {
      form.resetFields();
    };
    const onFill = () => {
      form.setFieldsValue({
        note: 'Hello world!',
        gender: 'male',
      });
    };


  return (
   <>
   <Form
{...layout}
form={form}
name="control-hooks"
onFinish={onFinish}
style={{ maxWidth: 600 }}
>

<Form.Item name="item_code" label="Item Code" rules={[{ required: true }]}>
  <Input/>
</Form.Item>
<Form.Item name="item_description" label="Item Description" rules={[{ required: true }]}>
  <Input/>
</Form.Item>
<Form.Item name="item_quantity" label="Quantity" rules={[{ required: true }]}>
  <Input/>
</Form.Item>
<Form.Item name="mechanic_name" label="mechanic_name" rules={[{ required: true }]}>
  <Select
    onChange={onMechanicChange}
    allowClear
  >
    {data.map(i=><Option value="Trimmer">i.mechanic_name</Option> )}
  </Select>
</Form.Item>
<Form.Item name="machine_number" label="Machine Number" rules={[{ required: true }]}>
<Input/>
</Form.Item>


<Form.Item {...tailLayout}>
  <Button type="primary" htmlType="submit">
    Submit
  </Button>
</Form.Item>
</Form> 
   </>
  )
}
