import React,{useEffect, useState} from 'react'
import { Button, Form, Select,Input } from 'antd';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';
const { Option } = Select;
const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};
export const PartIssue = () => {
  const[showUpdateButton,setShowUpdateButton]=useState(true);
   const[apiData,setApiData]=useState([]);
  //  const navigate = useNavigate()
   const [form] = Form.useForm();
   const onMechanicChange = (value) => {
    console.log(value)
  };
  useEffect(()=>{
    axios.get('http://localhost:5000/api/tool_table').then(res=>setApiData(res.data))
    // console.log(data,'data data data')
    // console.log(postData,'kkkkkkkkkkkkkkkkkkkkkk')
  },[])
  const onFinish = async(data) => {
    console.log(data,'onFinish')
    const updateRow = apiData.filter(i=>i.item_code==data.item_code)
    console.log(updateRow,'updateRow')
    let datas ={
      id:updateRow[0].id,    
      item_quantity:data.item_quantity,          
    }
    try {
      await axios.put('http://localhost:5000/api/issueTool', datas)
    } catch (error) {
      console.log(error)
    }
    setShowUpdateButton(preState=>!preState)
  };
    const onReset = () => {
      form.resetFields();
    };
    const onFill = () => {
      form.setFieldsValue({
        note: 'Hello world!',
        gender: 'male',
      });
    };


  return (
   <>
   <div style={{margin:"auto",width:"50%",marginTop:"3%"}}>
     <h2 style={{textAlign:"center"}}>Update Parts</h2>
    {!showUpdateButton &&<div style={{border:"1px solid red", marginBottom:"5%",width:"400px",height:"300px"}}>
    <Button onClick={()=>setShowUpdateButton(preState=>!preState)} style={{width:"50%",height:"50px",position:"relative",top:"132px",left:"106px"}}>Add Part</Button>
    </div>}
    {/* //////////////////////////////////////////////// */}
 {!showUpdateButton && 
   <Form
{...layout}
form={form}
name="control-hooks"
onFinish={onFinish}
style={{ maxWidth: 600 }}
>

<Form.Item name="item_code" label="Item Code" rules={[{ required: true }]}>
  <Input/>
</Form.Item>
<Form.Item name="item_description" label="Item Description" rules={[{ required: true }]}>
  <Input/>
</Form.Item>
<Form.Item name="item_quantity" label="Quantity" rules={[{ required: true }]}>
  <Input type='number'/>
</Form.Item>
<Form.Item name="mechanic_name" label="mechanic_name" rules={[{ required: true }]}>
  <Select
    onChange={onMechanicChange}
    allowClear
    >
    {apiData.map(i=><Option value="Trimmer">{i.mechanic_name}</Option> )}
  </Select>
</Form.Item>
<Form.Item name="machine_number" label="Machine Number" rules={[{ required: true }]}>
<Input/>
</Form.Item>


<Form.Item {...tailLayout}>
  <Button   type="primary" htmlType="submit">
    Update
  </Button>
</Form.Item>
</Form>}
    </div>
   </>
  )
}
