import { Button } from 'antd';
import React,{useState,useRef } from 'react';
import {QrReader}  from 'react-qr-reader';
  
export const QrCode=()=>{
    const[scanResultFile,setScanResultFile]=useState("");
    const qrRef = React.useRef<QrReader>(null)
  
  const handleError = ()=>{
    console.log('error in qr scanning')
  }
  const handleScanner = (result)=>{
    if(result){
    setScanResultFile(result)
  }
}
  
  const onScanFile = ()=>{
    qrRef.current?.openImageDialog()
  }

  return (
    <div className="App">
     <Button onClick={onScanFile}>Scan Qr</Button>
     <Button onClick={onScanFile}>Scan Qr</Button>
     {/* <QrReader
    innerRef={qrRef}
    delay={300}
    style={{width:"300px"}}
    onError={handleError}
    onScan={handleScanner}
    lagacyMode
    /> */}
    <h3>scan resultl: {scanResultFile}</h3>
    <h3>Hekko</h3>
    </div>
    
  );
}
