import React from 'react'
import { Chart } from 'react-charts'
import { Table,Form,Input,Select,Button } from 'antd'
export const MyChart=({downtimeData})=>{
  const data = React.useMemo(
    () => [
      {
        label: 'Downtime Minutes',
        data: [[0, 1], [1, 2], [2, 4], [3, 2], [4, 7]]
      },
    ],
    []
  )
  const secondChartData = React.useMemo(
    () => [
      {
        label: 'Downtime Minutes',
        data: [[0, 1], [1, 2], [2, 4], [3, 2], [4, 7]],
        dataType: "Line"
      },
    ],
    []
  )
 
  const axes = React.useMemo(
    () => [
      { primary: true, type: 'linear', position: 'bottom' },
      { type: 'bar', position: 'left' },
      
    ],
    []
  )
 
  // const lineChart = (
    // A react-chart hyper-responsively and continuously fills the available
    // space of its parent element automatically

    const onFinish=()=>{
      console.log('search')
    }
   return(
    <>
    <div
      style={{
        width: '75%',
        marginBottom: '20px'
      }}
      > 
     <Form
      //   className="tool_record_form"
      name="tool-form"
      layout="inline"
      onFinish={onFinish}
      // style={{
        //   maxWidth: 1200,
        // }}
        // initialValues={{
        //   price: {
          //     number: 0,
          //     currency: 'rmb',
          //   },
        // }}
      >
      
        <Form.Item
          name="category"
          label="Category"
          >
       <Select style={{width:"120px"}}>
            <Select.Option  value="demo">Demo</Select.Option>
            <Select.Option value="demo">Demo2</Select.Option>
            <Select.Option value="demo">Demo3</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item
          name="for_machine"
          label="For Machine"
          >
       <Select style={{width:"120px"}}>
            <Select.Option value="demo">Demo</Select.Option>
            <Select.Option value="demo">Demo2</Select.Option>
            <Select.Option value="demo">Demo3</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item
          name="part_code"
          label="Part Code"
          >
          <Input />
        </Form.Item>
        <Form.Item>
          <Button className="search_btn" htmlType="submit">
            Search
          </Button>
        </Form.Item>
      </Form>
      </div>
      <div
      style={{
        width: '40%',
        height: '300px'
      }}
    > 
      <Chart style={{width:"400px"}} data={data} axes={axes} />
      <Chart style={{width:"400px"}} data={secondChartData} axes={axes} />
    </div>
          </>
  )
}
