import React,{useState,useEffect} from "react";
import { Button, Form, Input, Radio } from "antd";
import { Link, Navigate, useNavigate } from "react-router-dom";

import styles from "./feature.module.css";
export const Signup = () => {
  const navigate = useNavigate();
  const initialValue = {
    name:"",
    email:"",
    password:""
  }
  const [signup,setSignup]=useState(initialValue)
  // const [error, setError] = useState("");

  // const layout = {
  //   labelCol: {
  //     span: 8
  //   },
  //   wrapperCol: {
  //     span: 16
  //   }
  // };

  // const validateMessages = {
  //   required: "${label} is required!",
  //   types: {
  //     email: "${label} is not a valid email!",
  //     number: "${label} is not a valid number!"
  //   },
  //   number: {
  //     range: "${label} must be between ${min} and ${max}"
  //   }
  // };

  const onFinish = async (value) => {
    if(!value.user.name || !value.user.email || !value.user.password){
      alert("error")
    }else{
      axios.post('http://localhost:5000/api/post',{
        name,
        email,
        password,
      }).then(()=>{
        setSignup({name:"",email:"",password:""})
      }).catch((err)=>alert(err,'error'));
      setTimeout(()=>{
        navigate('/qrcode');
      },500)
    }
  };

  return (
    <div className={styles.container}>
      <h2>Sign Up</h2>

      <Form
        className={styles.form_container}
        layout="vertical"
        // {...layout}
        name="nest-messages"
        onFinish={onFinish}
        style={{
          maxWidth: 600
        }}
        // validateMessages={validateMessages}
      >
        <Form.Item name={["user", "designation"]} label="Your Designation">
          <Radio.Group>
            <Radio.Button value="Supervisor">Supervisor</Radio.Button>
            <Radio.Button value="Machanic">Machanic</Radio.Button>
          </Radio.Group>
        </Form.Item>
        <Form.Item
          name={["user", "name"]}
          label="Name"
          rules={[
            {
              type: "name"
            }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={["user", "email_address"]}
          label="Email"
          rules={[
            {
              type: "email"
            }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={["user", "password"]}
          label="Password"
          rules={[
            {
              required: true
            }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          wrapperCol={{
            offset: 1
          }}
        >
          <Button
            className={styles.feature_btn}
            type="primary"
            htmlType="submit"
          >
            Register
          </Button>
        </Form.Item>
        <p className={styles.feature_footer_link}>
          <span>Already have an account?</span>
          <Link className={styles.link_class} to="/login">
            Login
          </Link>
        </p>
      </Form>
    </div>
  );
};
