import { Button } from 'antd';
import React,{useState,useRef } from 'react';
import {QrReader}  from 'react-qr-reader';
  
export const QrCode=()=>{
    const [data, setData] = useState('No result');

    return (
      <>
        <QrReader
          onResult={(result, error) => {
            if (!!result) {
              setData(result?.text);
            }
  
            if (!!error) {
              console.info(error);
            }
          }}
          style={{ width: '100%' }}
        />
        <p>hh</p>
        <p>{data}</p>
      </>
    );
}
