import React, { useEffect, useState } from "react";
import { Button, Form, Input,Modal,Select,Space, TimePicker } from "antd";
import { useNavigate } from "react-router-dom";
import styles from "./home.module.css";
import dayjs from 'dayjs';
import { NotificationConfirmation } from "./notificationConfirmation";
export const SupervisorForm = (props) => {
  const [open, setIsOpen] = useState(false);
  // const option = {Select}
  const navigate = useNavigate();
  const [form] = Form.useForm();

  const [selectedTime,setSelectedTime]=useState(dayjs(moment().format('hh:mm:ss')))
  const date = new Date();
  useEffect(()=>{
    setSelectedTime(moment().format('hh:mm:ss'))
    console.log(moment().format('hh:mm:ss'));
  },[date])
  

  const layout = {
    labelCol: {
      span: 8
    },
    wrapperCol: {
      span: 16
    }
  };

  const validateMessages = {
    required: "${label} is required!",
    types: {
      email: "${label} is not a valid email!",
      number: "${label} is not a valid number!"
    },
    number: {
      range: "${label} must be between ${min} and ${max}"
    }
  };
  useEffect(()=>{
    console.log(props.data,'inn home component++++++')
  },[])

  const { Option } = Select;
const handleChange = (value) => {
  console.log(`selected ${value}`);
};

  const handleOk = () => {
    setIsOpen(false);
  };

  const handleCancel = () => {
    setIsOpen(false);
  };

  const onFinish = (values) => {
    // setDataBase(values);
    // values.preventDefaul;
    // localStorage.setItem('items', JSON.stringify([ ...oldData, ...obj ]));
  

    let oldData = JSON.parse(localStorage.getItem("userData"));
    localStorage.setItem("userData", JSON.stringify([...oldData, values.user]));
    console.log(values, "-----------");
    if (values) {
      setIsOpen(true);
      // navigate("/notification_confirmation")
    }
  };
  return (
    <>
       {props.data ? <div className={styles.container}>
        {/* <Button className={styles.home_btn} type="primary">
          First
        </Button>
        <Button className={styles.home_btn} type="primary">
          Second
        </Button> */}
      <Form
      form = {form}
          className={styles.form_container}
          layout="vertical"
          name="nest-messages"
          onFinish={onFinish}
          initialValues={props.data}
          style={{
            maxWidth: 600
          }}
          validateMessages={validateMessages}
        >
          <Form.Item
            name={["user", "line_no"]}
            label="Line Number"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input disabled defaultValue={props.data.line_no}/>
          </Form.Item>
          <Form.Item
          defaultValue="props.data.machine_no"
            name={["name", "machine_no"]}
            // defaultValue={ props.data.machine_id}
            label="Machine Number"
            
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input disabled defaultValue={props.data.machine_no} />
          </Form.Item>
          <Form.Item
            name={["user", "machine_type"]}
            label="Machine Type"
            rules={[
              {
                required: true
              }
            ]}
          >
            <Input disabled defaultValue={props.data.machine_type}/>
          </Form.Item>
          <Form.Item
            name={["user", "start_time"]}
            label="Start Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <TimePicker defaultValue={dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')} size="large" />
          </Form.Item>
                <Form.Item
                  name={["user", "operation"]}
                  label="Operation"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input defaultValue={props.data.operation}/>
                </Form.Item>
                <Form.Item
            name={["user", "breakdown_time"]}
            label="Breakdown Time"
            rules={[
              {
                required: true
              }
            ]}
          >
            <TimePicker defaultValue={dayjs(moment().format('hh:mm:ss'), 'HH:mm:ss')} size="large" />
          </Form.Item>
          <Form.Item
                  name={["user", "breakdown_reason"]}
                  label="Breakdown Reason"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Select
    mode="multiple"
    style={{
      width: '100%',
    }}
    placeholder="select one country"
    defaultValue={['Overrunning machines']}
    onChange={handleChange}
    optionLabelProp="label"
  >
    <Option value="china" label="Wiring Fault">
      <Space>
       wiring fault
      </Space>
    </Option>
    <Option value="usa" label="Untrained operators">
      <Space>
      Untrained operators
      </Space>
    </Option>
    <Option value="japan" label="Poor maintenance">
      <Space>
      Poor maintenance
      </Space>
    </Option>
    <Option value="korea" label="Overrunning machines">
      <Space>
      Overrunning machines
      </Space>
    </Option>
  </Select>
                </Form.Item>
          <Form.Item
                  name={["user", "action_taken"]}
                  label="Action Taken"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input />
                </Form.Item>
          <Form.Item
                  name={["user", "part_replaced"]}
                  label="Part Replaced"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                   <Select
    mode="multiple"
    style={{
      width: '100%',
    }}
    placeholder="select one country"
    defaultValue={['wire']}
    onChange={handleChange}
    optionLabelProp="label"
  >
    <Option value="china" label="main Part">
      <Space>
       main Part
      </Space>
    </Option>
    <Option value="usa" label="Wire">
      <Space>
      Wire
      </Space>
    </Option>
    <Option value="japan" label="Head">
      <Space>
      Head
      </Space>
    </Option>
    <Option value="korea" label="machines">
      <Space>
      machines
      </Space>
    </Option>
  </Select>
                </Form.Item>
          <Form.Item
                  name={["user", "no_of_spare_parts"]}
                  label="No Of Spare Parts"
                  rules={[
                    {
                      required: true
                    }
                  ]}
                >
                  <Input />
                </Form.Item>
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              htmlType="submit"
            >
             Machine Repaired
            </Button>
          </Form.Item>
          <Form.Item
            wrapperCol={{
              ...layout.wrapperCol,
              offset: 3
            }}
          >
            <Button
              className={styles.home_btn}
              type="primary"
              htmlType="submit"
            >
              Submit Record
            </Button>
          </Form.Item>
        </Form>
        <Modal open={open} title="Reset Password Using Email"
          onOk={handleOk}
          onCancel={handleCancel}>
        <NotificationConfirmation/>
      </Modal>
      </div>:<h1>Coming soon....</h1>}
     
    </>
  );
};
